import React, { useRef, useCallback } from 'react';
import './App.css';
import { useEffect } from 'react';
import { useState } from 'react';

function App() {
  const datePickerElement = useRef();
  const datesElement = useRef();
  const monthElement = useRef();
  const nextMonthElement = useRef();
  const prevMonthElement = useRef();
  const daysElement = useRef();

  const [months] = useState([
    'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
  ])
  console.log('teste')
  let date = new Date();
  let day = date.getDate();
  let month = date.getMonth();
  let year = date.getFullYear();

  let selectedDate = date;
  let selectedDay = day;
  let selectedMonth = month;
  let selectedYear = year;

  const formatDate = useCallback(
    (date) => {
      let day = date.getDate();
      if (day < 10) {
        day = '0' + day;
      }
      let month = date.getMonth() + 1;
      if (month < 10) {
        month = '0' + month;
      }
      const year = date.getFullYear();
      return `${day} / ${month} / ${year}`;
    },
    [],
  );

  const populateDates = useCallback((e) => {
    daysElement.current.innerHtml = '';
    let amountDays = 31;
    if (month === 1) {
      amountDays = 28;
    }

    for (let index = 0; index < amountDays; index++) {
      const dayElement = document.createElement('div');
      daysElement.classList.add('day');
      daysElement.textContent = i + 1;
      daysElement.appendChild(dayElement);
    }
  }, [month])

  const toggleDatePicker = useCallback(
    (e) => {
      if (!e.target.closest('.dates')) {
        datesElement.current.classList.toggle('active')
      }
    },
    [],
  );

  const goToNextMonth = useCallback(
    (event) => {
      month += 1;
      if (month > 11) {
        month = 0;
        year += 1;
      }
      monthElement.current.textContent = `${months[month]} ${year}`;
    },
    [month, year],
  );

  const goToPrevMonth = useCallback(
    (event) => {
      month -= 1;
      if (month < 0) {
        month = 11;
        year -= 1;
      }
      monthElement.current.textContent = `${months[month]} ${year}`;
    },
    [],
  );

  useEffect(() => {
    populateDates();
    monthElement.current.textContent = `${months[month]} ${year}`;
    datePickerElement.current.addEventListener('click', toggleDatePicker);
    nextMonthElement.current.addEventListener('click', goToNextMonth);
    prevMonthElement.current.addEventListener('click', goToPrevMonth);
  }, [goToNextMonth, goToPrevMonth, month, months, populateDates, toggleDatePicker, year])

  return (
    <div className="date-picker" ref={datePickerElement}>
      <div className="selected-date">30/01/2020</div>
      <div className="dates" ref={datesElement}>
        <div className="month">
          <div className="arrows prev-month" ref={prevMonthElement}>&lt;</div>
          <div className="label-month" ref={monthElement}>February</div>
          <div className="arrows next-month" ref={nextMonthElement}>&gt;</div>
        </div>
        <div className="days" ref={daysElement}>
          <div className="day">1</div>
          <div className="day">2</div>
          <div className="day">3</div>
          <div className="day">4</div>
          <div className="day">5</div>
          <div className="day">6</div>
          <div className="day">7</div>
        </div>
      </div>
    </div>
  );
}

export default App;
