import React, { useRef, useCallback } from 'react';
import './App.css';
import { useEffect } from 'react';
import { useState } from 'react';

function App() {
  const datePickerElement = useRef();
  const datesElement = useRef();
  const monthElement = useRef();
  const nextMonthElement = useRef();
  const prevMonthElement = useRef();
  const daysElement = useRef();

  const [months] = useState([
    'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
  ])

  const [date, setDate] = useState(state => {
    let date = new Date();
    return { date, day: date.getDate(), month: date.getMonth(), year: date.getFullYear() }
  })

  let selectedDate = date.date;
  let selectedDay = date.day;
  let selectedMonth = date.month;
  let selectedYear = date.year;

  const formatDate = useCallback(
    (date) => {
      let day = date.getDate();
      if (day < 10) {
        day = '0' + day;
      }
      let month = date.getMonth() + 1;
      if (month < 10) {
        month = '0' + month;
      }
      const year = date.getFullYear();
      return `${day} / ${month} / ${year}`;
    },
    [],
  );

  const populateDates = () => {
    let amountDays = 31;
    if (date.month % 2 && date.month !== 1) {
      amountDays = 30;
    } else if (date.month === 1) {
      amountDays = 28;
    }

    let datesContainer = [];
    for (let index = 0; index < amountDays; index++) {
      const isSelectedDay = (
        selectedDay === (index + 1) &&
        selectedMonth === date.month &&
        selectedYear === date.year
      );
      const selectedClass = isSelectedDay ? 'selected' : '';
      console.log(selectedClass);
      datesContainer.push(<div key={index} className={`day ${selectedClass}`}>{index + 1}</div>);
    }

    return datesContainer;
  }

  const toggleDatePicker = useCallback(
    (e) => {
      if (!e.target.closest('.dates')) {
        datesElement.current.classList.toggle('active')
      }
    },
    [],
  );

  const goToNextMonth = useCallback(
    (event) => {
      setDate(state => {
        const returnedDate = { ...state };
        returnedDate.month += 1;
        if (returnedDate.month > 11) {
          returnedDate.month = 0;
          returnedDate.year += 1;
        }
        return returnedDate;
      })

    },
    [],
  );

  const goToPrevMonth = useCallback(
    (event) => {
      setDate(state => {
        const returnedDate = { ...state };
        returnedDate.month -= 1;
        if (returnedDate.month < 0) {
          returnedDate.month = 11;
          // returnedDate.year -= 1;
        }
        return returnedDate;
      })
      monthElement.current.textContent = `${months[date.month]} ${date.year}`;
    },
    [date.month, date.year, months],
  );

  useEffect(() => {
    console.log('loop');
    datePickerElement.current.addEventListener('click', toggleDatePicker);
    nextMonthElement.current.addEventListener('click', goToNextMonth);
    prevMonthElement.current.addEventListener('click', goToPrevMonth);

    return () => {
      datePickerElement.current.removeEventListener('click', toggleDatePicker);
      nextMonthElement.current.removeEventListener('click', goToNextMonth);
      prevMonthElement.current.removeEventListener('click', goToPrevMonth);
    }
  }, [goToNextMonth, goToPrevMonth, toggleDatePicker])


  useEffect(() => {
    console.log('loop2');
    monthElement.current.textContent = `${months[date.month]} ${date.year}`;
  }, [date.month, date.year, months])

  return (
    <div className="date-picker" ref={datePickerElement}>
      <div className="selected-date">30/01/2020</div>
      <div className="dates" ref={datesElement}>
        <div className="month">
          <div className="arrows prev-month" ref={prevMonthElement}>&lt;</div>
          <div className="label-month" ref={monthElement}>February</div>
          <div className="arrows next-month" ref={nextMonthElement}>&gt;</div>
        </div>
        <div className="days">
          {populateDates()};
        </div>
      </div>
    </div>
  );
}

export default App;
